import { useRef } from "react";
import { FaBars, FaTimes } from "react-icons/fa";
import "./css/main.css";

function Navbar() {
	const navRef = useRef();

	const showNavbar = () => {
		navRef.current.classList.toggle("responsive_nav");
	};

	return (
		<header>
			<h3>LOGO</h3>
			<nav ref={navRef}>
				<a href="/#"className="nav-text">Home</a>
				<a href="/#"className="nav-text">My work</a>
				<a href="/#"className="nav-text">Blog</a>
				<a href="/#"className="nav-text">About me</a>
				<a href="/#"className="nav-text">About me</a>
				<button
					className="nav-btn nav-close-btn"
					onClick={showNavbar}>
					<FaTimes />
				</button>
			</nav>
			<button className="nav-btn" onClick={showNavbar}>
				<FaBars />
			</button>
		</header>
	);
}

export default Navbar;
