import { useRef } from "react";
import { FaBars, FaTimes } from "react-icons/fa";
import "./css/main.css";
import Logo from '../images/logo.png';


function Navbar() {
	const navRef = useRef();

	const showNavbar = () => {
		navRef.current.classList.toggle("responsive_nav");
	};

	return (
		<header>
			<img style={{width:'200px'}} src={Logo} alt='logo'/>
			<div class="search-container">
    <form action="/action_page.php">
      <input type="text" placeholder="Search.." name="search"/>
      <button type="submit"><i class="fa fa-search"></i></button>
    </form>
  </div>
			
			<nav ref={navRef}><div className="nav-text-1">
				<a  href="/#"className="nav-text"> &nbsp;</a></div>
				<a href="/#"className="nav-text">Home</a>
				<a href="/#"className="nav-text">Technology</a>
				<a href="/#"className="nav-text">Kids</a>
				<a href="/#"className="nav-text">Recipe</a>
				<a href="/#"className="nav-text">Restuarnt</a>
				<button className='nav-btn-1'>Log in</button>
				<button className='nav-btn-2'>Sign in</button>
				<button
					className="nav-btn nav-close-btn"
					onClick={showNavbar}>
					<FaTimes />
				</button>
			</nav>
			<button className="nav-btn" onClick={showNavbar}>
				<FaBars />
			</button>
		</header>
	);
}

export default Navbar;
