import React from 'react';
import {useRef} from 'react';
import Nav from './Nav';
import './home.css';
import Banner from '../images/banner.png';
import Tech from '../images/tech.png';
import Kid from '../images/kid.png';
import Cook from '../images/cook.png';
import Cafe from '../images/cafe.png';



function Home() {
    const ref = useRef(null);

  const handleClick = () => {
    ref.current?.scrollIntoView({behavior: 'smooth'});
  };
  return (
    <div>
      
<div class="section-1 box"><Nav/>

    <div className='container-h'>
      <button className='btn-1'>Login</button>
      <br/>~or~<br/>
      <button className='btn-2'>Register</button>
      <br/>
      <img className='profile' src={Profile}/>



    </div>
    <center><button className='btn' onClick={handleClick}><div class="chevron"></div>
    <div class="chevron"></div>
    <div class="chevron"></div></button></center>


</div>


<div className="section-2 box" ref={ref}>
<div className='container-1'>
      <div className='container-box'style={{marginRight:'10vw'}}>
        <img className='anime-image' src={Cafe}/>Resturant
      </div>
      <div className='container-box'>
        <img className='anime-image' src={Cook}/> Recipece
        </div>
      <div className='container-box'>
        <img className='anime-image' src={Kid}/>Kids
        </div>
      <div className='container-box'>
        <img className='anime-image' src={Tech}/> Technology
        </div>
    </div>
    <br/>
    <div className='container'><img className='banner-img' src={Banner}/></div>
    <br/>
    <div className='container-1' style={{height:'12vw',backgroundColor:'#ffffff00'}}>
      
      
    </div>
    <br/>
    

  </div>

</div>
  );
}

export default Home;
